queryList.query:
PREFIX ns: <https://data.lacity.org#>

SELECT ?area (COUNT(?crimeType) AS ?totalCrimes)
WHERE {
  ?crime ns:inArea ?neighborhood ;
         ns:crimeOccurred ?crimeType .
  ?neighborhood ns:areaDescription ?area;
}
GROUP BY ?area
ORDER BY DESC(?totalCrimes)

PREFIX ns: <https://data.lacity.org#>

SELECT ?area ?crimeDesc (COUNT(?crimeType) AS ?totalCrimes)
WHERE {
  ?crime ns:inArea ?neighborhood;
         ns:crimeOccurred ?crimeType.
  ?neighborhood ns:areaDescription ?area.
  ?crimeType ns:crimeDescription ?crimeDesc;
}
GROUP BY ?area ?crimeDesc
ORDER BY DESC(?totalCrimes)

PREFIX ns: <https://data.lacity.org#>

SELECT ?year ?area (COUNT(?crimeType) AS ?numCrimes)
WHERE {
  ?crime ns:inArea ?neighborhood;
         ns:crimeOccurred ?crimeType;
         ns:reportedOn ?date;
         BIND(YEAR(?date) AS ?year).
  ?neighborhood ns:areaDescription ?area.
}
GROUP BY ?year ?area
ORDER BY ?year ?area
