PREFIX cds: <https://data.lacity.org/resource/>
PREFIX hds: <https://www.zillow.com/research/data#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT ?area (AVG(?price) AS ?avgPrice) (count(distinct ?crimeType) as ?count) ?year
WHERE {
?crime rdf:type cds:CrimeRecord;
	cds:area_name ?area ;
	cds:crm_cd_desc ?crimeType;
	cds:date_rptd ?date.
?housing rdf:type hds:HousingRecord;
	hds:hasHPI ?price;
	hds:inRegion ?area;
	hds:onDate ?dateTime;
	BIND(STR(YEAR(?dateTime)) AS ?year)
	FILTER(REGEX(?date, ?year) &&
	?year = "2020").
}
GROUP BY ?area ?year
ORDER BY DESC(?count) DESC(?avgPrice)
LIMIT 5

PREFIX cds: <https://data.lacity.org/resource/>

SELECT ?area  ?premiseDesc (COUNT(?statusDesc) as ?count)
WHERE {
  ?crime cds:area_name ?area;
         cds:crm_cd ?crimeType;
         cds:premis_desc ?premiseDesc;
         cds:status_desc ?statusDesc;
         cds:date_rptd ?date.
         BIND(SUBSTR(?date,0,5) AS ?year).
         FILTER(REGEX(?statusDesc, "Invest Cont")).

}
GROUP BY ?area ?premiseDesc
ORDER BY DESC(?count)

PREFIX cds: <https://data.lacity.org/resource/>
PREFIX hds: <https://www.zillow.com/research/data#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>

ASK {
  # Check if there is a crime with a weapon used and a victim aged 25 or younger
  ?crime rdf:type cds:CrimeRecord ;
         cds:vict_age ?victAge ;
         cds:weapon_desc ?weaponUsed ;
         cds:area_name ?areaName .

  FILTER(xsd:integer(?victAge) > 25 && ?weaponUsed != "")

  # Check if there is a housing area with a high average house price in the same region as the crime
  ?housing rdf:type hds:HousingRecord ;
           hds:hasHPI ?housePrice ;
           hds:inRegion ?areaName ;
           hds:onDate ?onDate .

  FILTER(?housePrice > 800000)

  # Check if there is another housing area in the same city and state as the high-priced area
  ?otherHousing rdf:type hds:HousingRecord ;
                hds:hasHPI ?otherHousePrice ;
                hds:inCity ?city ;
                hds:inState ?state ;
                hds:onDate ?onDate .

  FILTER(?otherHousePrice < 500000)

  # Check if there is another crime in the same city and state as the high-priced area with a similar weapon used
  ?otherCrime rdf:type cds:CrimeRecord ;
              cds:weapon_desc ?weaponUsed ;
              cds:area_name ?areaName .

  FILTER(?otherCrime != ?crime)
}

PREFIX cds: <https://data.lacity.org/resource/>
PREFIX hds: <https://www.zillow.com/research/data#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX schema: <http://schema.org/>

CONSTRUCT {
  ?crime rdf:type schema:Victim.
  ?crime schema:age ?victAge.
  ?crime schema:descent ?vict_descent.
  ?crime schema:inArea ?areaName .
  ?crime schema:usingWeapon ?weaponUsed .
  ?house schema:housePriceIndex ?hasHPI .
  ?house schema:year ?year .
  ?house schema:inCity ?inCity .
  ?house schema:inState ?inState ;
}
WHERE {
?crime rdf:type cds:CrimeRecord;
	cds:area_name ?area ;
    cds:vict_age ?victAge;
    cds:vict_descent ?vict_descent;
	cds:date_rptd ?date;
    cds:area_name ?areaName;
    cds:weapon_desc ?weaponUsed .

?house rdf:type hds:HousingRecord;
	hds:hasHPI ?price;
	hds:inRegion ?area;
	hds:onDate ?dateTime;
    hds:hasHPI ?hasHPI ;
    hds:inCity ?inCity ;
    hds:inState ?inState ;
	BIND(STR(YEAR(?dateTime)) AS ?year)
	FILTER(REGEX(?date, ?year)).
}
